#include "AsyncWorker_Functor.h"

AsyncWorker_Functor::AsyncWorker_Functor(async_queue<DataDescPtr>& input, async_queue<DataDescPtr>& output, 
  const Functor& functor, const std::shared_ptr<ILogger>& logger)
: IAsyncWorker(input, output, logger)
, m_functor(functor)
{
  __DEV_CALLSTACK_FUNC__;
}

DataDescPtr AsyncWorker_Functor::_processImpl(const DataDescPtr data)
{
  __DEV_CALLSTACK_FUNC__;
  return m_functor(this, data);
}
