#include "ErrorDataDesc.h"

ErrorDataDesc::ErrorDataDesc(const IBaseFilter* pFilter, const eErrorCategory category, const uint32_t errorCode)
:  IDataDesc(IDataDesc::eType::DATA_TYPE_ERROR)
,  m_pFilter(pFilter)
,  m_category(category)
,  m_errorCode(errorCode)
{
  __DEV_CALLSTACK_FUNC__;
}

bool ErrorDataDesc::init(const IBaseFilter* pFilter, const eErrorCategory category, const uint32_t errorCode)
{
  __DEV_CALLSTACK_FUNC__;
  m_pFilter = pFilter;
  m_category = category;
  m_errorCode = errorCode;
  return true;
}
