#include "ProgressDataDesc.h"

ProgressDataDesc::ProgressDataDesc(const IBaseFilter* pReporter, const sHarvestProgress& progressInfo)
:  IDataDesc(IDataDesc::eType::DATA_TYPE_PROGRESS)
,  m_pReporter(pReporter)
,  m_progressInfo(progressInfo)
{
  __DEV_CALLSTACK_FUNC__;
}

bool ProgressDataDesc::init(const IBaseFilter* pReporter, const sHarvestProgress& progressInfo)
{
  __DEV_CALLSTACK_FUNC__;
  m_pReporter = pReporter;
  m_progressInfo = progressInfo;
  return true;
}
