#ifndef IGapGenerator_h__
#define IGapGenerator_h__

#include "../../../harvlib_defines.h"

/**
 * Basic class for gaps generators
 */
class IGapGenerator
{
public:
  /**
   * IGapGenerator destructor
   */
  virtual ~IGapGenerator() = default;

  /**
   * Generates gap
   * @param   recordIndex record index
   * @return  generated gap (in nanoseconds)
   */
  timediff_t generate(const uint32_t recordIndex) const;

protected:
  IGapGenerator();
  virtual timediff_t _generateImpl(const uint32_t recordIndex) const = 0;
};

#endif //IGapGenerator_h__
