#include "SamplesGenerator_Func.h"

SamplesGenerator_Func::SamplesGenerator_Func(const Functor functor)
: ISamplesGenerator()
, m_functor(functor)
{
  __DEV_CALLSTACK_FUNC__;
  __ASSERT__(functor != nullptr && "'functor' cannot be nullptr here");
}

double SamplesGenerator_Func::_generateRawImpl(const uint64_t sampleIndex)
{
  __DEV_CALLSTACK_FUNC__;
  return m_functor(sampleIndex);
}
