#include "SamplesGenerator_MinMax.h"

SamplesGenerator_MinMax::SamplesGenerator_MinMax(const sample_t minValue, const sample_t maxValue)
: ISamplesGenerator()
, m_min(minValue)
, m_max(maxValue)
{
  __DEV_CALLSTACK_FUNC__;
}

double SamplesGenerator_MinMax::_generateRawImpl(const uint64_t sampleIndex)
{
  __DEV_CALLSTACK_FUNC__;
  return (sampleIndex % 2 == 0) ? m_min : m_max;
}
