#include "TimeRangeFetcher_ForensicLog.h"

#include <DataTypes/ForensicLog/MsgForensicLog.h>

TimeRangeFetcher_ForensicLog::TimeRangeFetcher_ForensicLog(const std::shared_ptr<ILogger>& logger)
  : ITimeRangeFetcher(logger)
{
  __DEV_CALLSTACK_FUNC__;
}

bool TimeRangeFetcher_ForensicLog::_getLowerTimeImpl(const PSFDataElement& dataElement, timestamp_t* result) const
{
  __DEV_CALLSTACK_FUNC__;
  if (!dataElement.isInstanceOf(PDB_eMessageType_t::MSG_FORENSIC_LOG_V0))
  {
    const message_type_t messageType = dataElement.getHeader()->messageType;
    m_logger->error(__THIS_FUNC__, eErrorCategory::ERROR_CATEGORY_LIBRARY, eHarvLibErrorCode::HARVLIB_ERROR_INVALID_MESSAGE_TYPE,
      "Invalid message type (0x%08x) fed to fetcher. Should be MSG_FORENSIC_LOG_V0 (0x%08x)", 
      (uint32_t)messageType, (uint32_t)PDB_eMessageType_t::MSG_FORENSIC_LOG_V0);
    return false;
  }

  const void* pData = nullptr;
  dataElement.getData(&pData);
  const MsgForensicLog::Header* const pMsgHeader = reinterpret_cast<const MsgForensicLog::Header*>(pData);
  *result = pMsgHeader->timestamp;
  return true;
}

