#include "harvester_utils.h"

#include <libmseed.h>

namespace harvester_utils
{
  uint8_t map_media_type_to_mseed_encoding(const PDB_eMediaType_t mediaType)
  {
    __DEV_CALLSTACK_FUNC__;
    switch (mediaType)
    {
      case PDB_eMediaType_t::MEDIA_UNCOMPRESSED: return DE_INT32;
      case PDB_eMediaType_t::MEDIA_STEIM1: return DE_STEIM1;
      case PDB_eMediaType_t::MEDIA_STEIM2: return DE_STEIM2;
      default: __ASSERT__("Unhandled media type");
    }
    __ASSERT__(false && "This code should never be reached!");
    return 0;
  }
}
