#ifndef IIODriver_h__
#define IIODriver_h__

#include "../libio_defines.h"
#include "../Logger/ILogger.h"

#include <memory>

class IIODriver
{
protected:
  IIODriver(const std::shared_ptr<ILogger>& logger) : m_logger(logger) {}

public:
  virtual ~IIODriver() {}

public:
  virtual uint64_t getSize() const = 0;
  virtual size_t readBlocks(io_offset_t offset, const size_t nBytes, void* pBuffer) = 0;
  virtual size_t writeBlocks(io_offset_t offset, const size_t nBytes, const void* pBuffer) = 0;

protected:
  const std::shared_ptr<ILogger> m_logger;
};

#endif //IIODriver_h__
