#include "MsgDigitizerStart.h"

MsgDigitizerStart::MsgDigitizerStart()
:  IPDBMsgObject(PDB_eMessageType_t::MSG_DIGITIZER_START_V0)
{
  __DEV_CALLSTACK_FUNC__;
}

bool MsgDigitizerStart::init(const Header& header)
{
  __DEV_CALLSTACK_FUNC__;
  //tss_pdblib::calculate size required for Header
  const size_t totalSize = sizeof(Header);
  char* pBuffer = this->_alloc_buffer(totalSize);
  if (pBuffer != nullptr)
  {
    Header* const headerPtr = reinterpret_cast<Header*>(pBuffer);
    ::memcpy(headerPtr, &header, sizeof(Header));
    return true;
  }

  //tss_pdblib::failed to allocate buffer -> return false;
  return false;

}
