#include "MsgHealthDigest.h"

MsgHealthDigest::MsgHealthDigest()
:  IPDBMsgObject(PDB_eMessageType_t::MSG_HEALTH_DIGEST_V0)
{
  __DEV_CALLSTACK_FUNC__;
}

bool MsgHealthDigest::init(const Header& header)
{
  __DEV_CALLSTACK_FUNC__;
  //tss_pdb::allocate buffer to kepp all the data
  char* pBuffer = this->_alloc_buffer(sizeof(header));
  if (pBuffer == nullptr)
    return false;

  Header* const headerPtr = reinterpret_cast<Header*>(pBuffer);
  ::memcpy(headerPtr, &header, sizeof(header));
  return true;
}
