#ifndef IPSFSectionUpdateDelegate_h__
#define IPSFSectionUpdateDelegate_h__

#include "../psflib_defines.h"

#include <Logger/ILogger.h>

#include <memory>

class PSFSection;
class IPSFSectionUpdateDelegate
{
public:
  virtual ~IPSFSectionUpdateDelegate() = default;

  virtual bool shouldUpdate(PSFSection* pSection, const size_t dataWritten) = 0;
  virtual void onSectionUpdated(PSFSection* pSection) = 0;

protected:
  IPSFSectionUpdateDelegate(const std::shared_ptr<ILogger>& logger);

protected:
  const std::shared_ptr<ILogger> m_logger;
};

#endif //IPSFSectionUpdateDelegate_h__
