#ifndef DigitizerInfo_h__
#define DigitizerInfo_h__

#pragma pack(push, 1)
struct DigitizerInfo
{
  static const uint8_t VERSION = 1; // current version
  uint8_t version;    // packet version
  char serial[6];     // digitizer serial number. E.g: 001234
  char bin[32];       // digitizer BIN number; null-padded at end
  char firmware[16];  // <major>.<minor>.<build>[-RC]; null-padded at the end

  char network[2];    // network name; null-padded at end
  char station[5];    // station name; null-padded at end
  uint8_t n_channels; // number of channels defined in channels member; 0-4.
  struct ChannelInfo
  {
    char name[3];     // channel code; null-padded at end
    char location[2]; // location code of channel; null-padded at end
  } channels[4];
};
#pragma pack(pop)

#endif // DigitizerInfo_h__
