#ifndef IResponseBufferProvider_hpp__
#define IResponseBufferProvider_hpp__

#include "LiveSohNotificationData.h"
#include "DigitizerInfo.h"
#include <DataTypes/SensorTimeSeries/MsgSensorInput.h>

class IResponseBufferProvider
{
public:
  IResponseBufferProvider() = default;
  virtual ~IResponseBufferProvider() = default;

  // returns the buffer to store Time Series response/packet (note: should be I/O aligned (32 bytes))
  virtual MsgSensorInput::Header* getTimeSeriesBuffer() = 0;
  // returns the buffer to store current SOH response (note: should be I/O aligned (32 bytes))
  virtual LiveSohNotificationData* getSohBuffer() = 0;
  // returns the buffer to store digitizer info (note: should be I/O aligned (32 bytes))
  virtual DigitizerInfo* getInfoBuffer() = 0;
};

#endif // IResponseBufferProvider_hpp__
