#ifndef ISerialPort_hpp__
#define ISerialPort_hpp__

#include <stdint.h>

class ISerialPort
{
public:
  ISerialPort() = default;
  virtual ~ISerialPort() = default;

  virtual uint32_t send(const void* buffer, const uint32_t size) = 0;
  virtual uint32_t receive(void* buffer, const uint32_t size) = 0;
}; 

#endif // ISerialPort_hpp__
