#ifndef HarvestParamsParser_h__
#define HarvestParamsParser_h__

#include "IJSONParser.h"

class HarvestParamsParser: public IJSONParser<sHarvestParams>
{
public:
  HarvestParamsParser(const std::shared_ptr<ILogger>& logger);
  virtual ~HarvestParamsParser() = default;

protected:
  bool _parseRange(const rapidjson::Value& value, sHarvestParams& output) const;
  bool _parseOutputPath(const rapidjson::Value& value, sHarvestParams& output) const;
  bool _parseOutputPattern(const rapidjson::Value& value, sHarvestParams& output) const;
  bool _parseDuration(const rapidjson::Value& value, sHarvestParams& output) const;

  bool _parseBoundary(const rapidjson::Value& value, sHarvestParams::sBoundary& output) const;
};

#endif //HarvestParamsParser_h__
